import React from 'react';
import styled from "styled-components";
//기본 프로필이미지
import profileDefault from '../assets/images/profile_default.png'

const InsertNewCommentSection = styled.section`
    box-sizing: border-box;
    padding:15px;
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    border-radius: 10px;
    box-shadow: 2px 2px 2px 2px rgba(190,190,190,0.5);
`;

const ProfileAvatar = styled.img`
    width: 50px;
    height: 50px;
    border-radius: 50%;
`;
const InputContainer = styled.section`
    margin: 0px 10px 0px 10px;
    display: flex;
    flex-direction: column;
`
const NameInput = styled.input`
    border-style: solid;
    border-radius: 5px;
    border-color: rgba(190,190,190,0.5);
    margin-bottom: 3px;
`;
const ContentInput = styled.textarea`
    border-radius: 5px;
    border-color: rgba(190,190,190,0.5);
    height: 10vh;
    min-height: 1.2rem;
`;

const SubmitButton = styled.button`
    height: 2rem;
`;


const InsertNewComment = () => {
    return (
        <InsertNewCommentSection>
            <ProfileAvatar src={profileDefault}></ProfileAvatar>
            <InputContainer>
                <NameInput placeholder='이름을 입력하세요.'></NameInput>
                <ContentInput placeholder='내용을 입력하세요.'></ContentInput>
            </InputContainer>
            <SubmitButton>등록하기</SubmitButton>
        </InsertNewCommentSection>
    );
}

export default InsertNewComment;
