import axios from 'axios';
const rootUrl = 'http://localhost:4999/guestbook';

//파일을 axios로 서버에 보낼 때는 formData로 보내야 한다 ?? => 이건 아닌것같다.. 
export const postNewGuestbookContent = async ({name,content,profileImage}) => {
    const url = rootUrl;
    //서버에 첨부파일을 보낼 때는 헤더 content-type 붙이는게 필수다. ..? 
    const headers = {
        'Content-Type' : 'multipart/form-data'
    }
    const data = {
        // name: name,
        // content: content,
        profileImage: profileImage
    };
    const response = await axios.post(url, data, headers);
    return response.data;
}

export const getAllGuestbookContents = async () => {
    const url = rootUrl;
    const response = await axios.get(url);
    return response.data;
}
