    box-sizing: border-box;
    margin: 10px;
    padding:15px;
    width: 80%;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    border-radius: 10px;
    box-shadow: 2px 2px 2px 2px rgba(190,190,190,0.5);
`;
//프로필 이미지 디자인
export const ProfileAvatar = styled.img`
    width: 10vh;
    height: 10vh;
    border-radius: 50%;
    cursor: pointer;
`;
//이름, 내용을 묶은 section
