    margin: 0px 10px 0px 10px;
    flex-grow: 1;
    max-width: 500px;
    height:15vh;
    min-height: 50px;
    display: flex;
    flex-direction: column;
    justify-content: center;
`
//이름 입력 input 디자인
export const NameInput = styled.input`
    padding: 3px;
    border-style: solid;
    border-radius: 5px;
    border-color: rgba(190,190,190,0.5);
    margin-bottom: 3px;
    font-family: 'Noto Sans KR', sans-serif;
`;
//내용 입력 textarea 디자인
export const ContentInput = styled.textarea`
    padding: 3px;
    border-radius: 5px;
    border-color: rgba(190,190,190,0.5);
    height: 10vh;
    min-height: 1.2rem;
    font-family: 'Noto Sans KR', sans-serif;
`;
//등록하기 버튼 디자인
export const SubmitButton = styled.button`
    height: 2rem;
    cursor: pointer;
    padding: 10px;
    border-radius: 20px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    font-family: 'Noto Sans KR', sans-serif;
`;
